import React, { Component } from "react";
import "./App.css";
import "./Factors.js";
import Breadcrumb from "react-bootstrap/Breadcrumb";
import BreadcrumbItem from "react-bootstrap/BreadcrumbItem";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import ProblemCard from "./ProblemCard";
import CardGroup from "./CardGroup";
import Jumbotron from "react-bootstrap/Jumbotron";
import Button from "react-bootstrap/Button";
import Display from "./Display";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cards: true,
      displayProblem: "",
      displayTitle: "",
      displaySolution: "",
      showProblem: true
    };
  }
  showSolution() {
    this.setState(prevState => ({
      showProblem: false
    }));
  }
  changeDisplay(title, text, solution) {
    this.setState(prevState => ({
      cards: !prevState.cards,
      displayTitle: title,
      displayProblem: text,
      displaySolution: solution
    }));
    console.log(this.state.displaySolution);
  }
  render() {
    this.cardsInstance = (
      <CardGroup onClick={(i, e, o) => this.changeDisplay(i, e, o)} />
    );
    this.displayInstance = (
      <div class="main-display">
        <Display
          displayTitle={this.state.displayTitle}
          displayProblem={this.state.displayText}
          displaySolution={this.state.displaySolution}
          onClick={() => this.showSolution}
          showProblem={this.state.showProblem}
        />
      </div>
    );

    console.log(this.state.displayTitle);
    return (
      <div className="App">
        <MainNav />

        <header className="App-header">
          {this.state.cards ? this.cardsInstance : this.displayInstance}
        </header>
      </div>
    );
  }
}
class MainNav extends Component {
  render() {
    return (
      <div className="navbar">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="./">Project Euler Solutions</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="https://projecteuler.net/archives">
                Archievs
              </Nav.Link>
              <Nav.Link href="#link">About</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>
      </div>
    );
  }
}
export default App;
